function listeCheck(box,ligne) {
	var t = document.getElementById("id2del");

for(a=0;a<tb_supp.length;a++){
	
var boxid = document.getElementById(a);
   if (boxid.checked){
	   tb_supp[a]=boxid.value;	
  	}else{
		tb_supp[a]="";
	}
}
t.value=tb_supp;
}

