// confirmation de la sélection avant envoi
function jeconfirme(){
document.getElementById('t').value=this.firstChild.data;
if(!confirm('Please confirm your company name. '+this.firstChild.data)){/*return false*/document.getElementById('t').value="";}
}

// affichage automatique des items correspondants
function go(alpha){ 
var affiche=document.getElementById('liste').getElementsByTagName('li');
var total=affiche.length;

// à chaque caractère tapé, on vide la liste de résultats précédente
for(i=total-1;i>=0;i--){
document.getElementById('liste').removeChild(affiche[i])
};

// on remplace "virtuellement" les caractères accentués par des voyelles simples
// dans le tableau de données et dans la saisie
for(i=0;i != tab.length;i++){  
tab2=tab[i].replace(/[àâä]/gi,'a'); 
tab2=tab2.replace(/[éèêë]/gi,'e'); 
tab2=tab2.replace(/[ïî]/gi,'i'); 
tab2=tab2.replace(/[ôö]/gi,'o'); 
tab2=tab2.replace(/[ùüû]/gi,'u'); 
alpha=alpha.replace(/[àâä]/gi,'a'); 
alpha=alpha.replace(/[éèêë]/gi,'e'); 
alpha=alpha.replace(/[ïî]/gi,'i'); 
alpha=alpha.replace(/[ôö]/gi,'o'); 
alpha=alpha.replace(/[ùüû]/gi,'u'); 

// dès qu'une correspondance est trouvée
// entre une saisie et le début d'une entrée du tableau ...
if(tab2.toLowerCase().indexOf(alpha.toLowerCase())==0){ 
if(alpha){
// on crée un nouvel item dans la liste de résultats
// et on lui donne pour contenu l'élément du tableau en relation
var el=document.createElement("li"); 
var adr=document.createElement("a"); 
var tx=document.createTextNode(tab[i]); 

// ajouter avant le "?" le nom de la page de destination si elle est différente de la page en cours;
// j'ai nommé arbitrairement les variables "commune" et "code", en divisant en 2 le texte de l'item
//adr.setAttribute("href","?commune="+tab[i].substring(0,tab[i].indexOf('(')-1)+'&amp;code='+tab[i].substring(tab[i].indexOf('(')+1,tab[i].length-1)); 
adr.onclick=jeconfirme;
adr.appendChild(tx); 
el.appendChild(adr); 
document.getElementById('liste').appendChild(el)};};
};

// si aucune correspondance n'existe, on crée un item "aucun résultat"
if((affiche.length==0) || (!alpha)){
var el=document.createElement("li"); 
var tx=document.createTextNode("Your company name wasn\'t found. Enter your company name it will be automatically add with your subscription request.");  
el.appendChild(tx);
document.getElementById('liste').appendChild(el)};
}
</script>
 



