<script language="JavaScript" type="text/JavaScript">
 testS = "N";

function makeOtherS(valList, testS){ 
	
	if(valList=="otherS" ){
		//if(testS.value=="N"){
		if(testS=="N"){
			chx('S');
		}	
		testS = "Y";  //testS.value = "Y";
		
	}else{
		 var div = document.getElementById("tbl");
        if( div ){
                while( div.hasChildNodes() )
                 div.removeChild( div.childNodes[0] );
   		} 
				
		testS = "N"; //testS.value = "N";
	}/**/
	
}

      function chx(nbInfo) {
        var div = document.getElementById( "contenu"+nbInfo );
        if( div ){
                while( div.hasChildNodes() )
                 div.removeChild( div.childNodes[0] );
   		}
			
        var div = document.getElementById( ("contenu"+nbInfo) );
		div.className="smalltext";	
					
      if((div)){ 
	  	//var div1 = document.getElementById("conteneurS");// get the reference for the body		
        var tbl = document.createElement("table");// creates a <table> element and a <tbody> element
		tbl.id="tbl";
		tbl.align="center";
		tbl.width="100%";
		tbl.cellPadding="0";
		tbl.cellSpacing="0";
        var tblBody = document.createElement("tbody");
		var row = document.createElement("tr");// creates a table row
		
		var cell = document.createElement("td");
		cell.className="smalltext";
		cell.style.padding="2px";
