function addBookmark(){
	if (document.all) {window.external.AddFavorite(location.href, document.title);}
	else if (window.sidebar){window.sidebar.addPanel(document.title, location.href, "");}
}


function hidestatus(){ 
window.status='Xerox - Xerox Alliance Partner Portal' 
return true 
} 
if (document.layers) 
document.captureEvents(Event.MOUSEOVER | Event.MOUSEOUT | Event.MOUSEDOWN) 
document.onmouseover=hidestatus 
document.onmouseout=hidestatus 
document.onmousedown=hidestatus 



function MM_openBrWindow(theURL,winName,features) { //v2.0
  window.open(theURL,winName,features);
}


function listeCtry(formName,nbCtry) {
var sortie = '';
for(a=0;a<nbCtry;a++){
   if (eval("document."+formName.name+".cntry"+a+".checked")){
		if(sortie != ''){
  		sortie+=', '+eval("document."+formName.name+".cntry"+a+".value");
		}else{
		sortie+=eval("document."+formName.name+".cntry"+a+".value");
		}		
  	}
}
formName.country.value=sortie;
}

function confirmation(msg,URLredir){
	if(confirm(msg)){
	window.location.href=URLredir;
	return false;
	}else{
	return true;
	}
}

function MM_findObj(n, d) { //v4.01
  var p,i,x;  if(!d) d=document; if((p=n.indexOf("?"))>0&&parent.frames.length) {
    d=parent.frames[n.substring(p+1)].document; n=n.substring(0,p);}
  if(!(x=d[n])&&d.all) x=d.all[n]; for (i=0;!x&&i<d.forms.length;i++) x=d.forms[i][n];
  for(i=0;!x&&d.layers&&i<d.layers.length;i++) x=MM_findObj(n,d.layers[i].document);
  if(!x && d.getElementById) x=d.getElementById(n); return x;
}
function MM_validateForm() { //v4.0
  var i,p,q,nm,test,num,min,max,errors='',args=MM_validateForm.arguments;
  for (i=0; i<(args.length-2); i+=3) { test=args[i+2]; val=MM_findObj(args[i]);
    if (val) { nm=val.name; if ((val=val.value)!="") {
      if (test.indexOf('isEmail')!=-1) { p=val.indexOf('@');
        if (p<1 || p==(val.length-1)) errors+='- '+nm+'must contain an e-mail address.\n';
      } else if (test!='R') { num = parseFloat(val);
        if (isNaN(val)) errors+='- '+nm+' MUSTcontain a number.\n';
        if (test.indexOf('inRange') != -1) { p=test.indexOf(':');
          min=test.substring(8,p); max=test.substring(p+1);
          if (num<min || max<num) errors+='- '+nm+' Must contain a number between '+min+'  and  '+max+'.\n';
    } } } else if (test.charAt(0) == 'R') errors += '- '+nm+' .\n'; }
  } if (errors) alert(':\n'+errors);
  document.MM_returnValue = (errors == '');
}


//-->

function decision(message, url)
{
	if(confirm(message)) location.href = url;
}

function switch_spoiler(div2)
{
	var divs = div2.getElementsByTagName('div');
	var div3 = divs[0];
	if (div3.style.visibility == 'visible')
		div3.style.visibility = 'hidden';
	else
		div3.style.visibility = 'visible';
	return true;
}
