function visible(valueListe,id){ // alert(valueListe);

      if ( (document.getElementById(id).style.display == "none") && (valueListe) == "MPS" ){
         document.getElementById(id).style.display = "";
      } else { 
         document.getElementById(id).style.display = "none";
      }
