      <script type="text/javascript">
	  
		function alertselected(selectobj){
			
			var selectValue = selectobj.value;
			var TabUS = new Array("Alabama","Alaska","Arizona","Arkansas","California","Colorado","Connecticut","Delaware","District of Columbia","Florida","Georgia","Hawaii","Idaho","Illinois","Indiana","Iowa","Kansas","Kentucky","Louisiana","Maine","Maryland","Massachusetts","Michigan","Minnesota","Mississippi","Missouri","Montana","Nebraska","Nevada","New Hampshire","New Jersey","New Mexico","New York","North Carolina","North Dakota","Ohio","Oklahoma","Oregon","Pennsylvania","Rhode Island","South Carolina","South Dakota","Tennessee","Texas","Utah","Florida","Virginia","Washington","West Virginia","Wisconsin","Wyoming","Newfoundland","Prince Edward Island","Nova Scotia","New Brunswick","Quebec","Ontario","Manitoba","Saskatchewan","Alberta","British Columbia","Northwest Territories","Nunavut","Yukon");
			
			var TabCanada = new Array("Alberta", "British Columbia", "Manitoba","New Brunswick",  "Newfoundland and Labrador", "Northwest Territories", "Nova Scotia", "Nunavut", "Ontario", "Prince Edward Island", "Quebec", "Saskatchewan", "Yukon");
			
			// detection du country choisi
			if(selectValue == "United States of America"){
				//var rtnvalue = selectValue +" TabUS \n";
				var tbListestates = TabUS ;
			}else if(selectValue == "Canada_English" || selectValue == "Canada_Français" ){
				//var rtnvalue = selectValue +" TabCanada \n";
				var tbListestates = TabCanada ;
			}else{
				var rtnvalue = selectValue +" Not US \n"
				var tbListestates = new Array("Not US") ;
				
			}
			//alert(rtnvalue);//
		
			// changement du champ States
			var myselect=document.getElementById("state");
			myselect.options.length=0
			
		
		// placement des options dans la liste
			for (var i=0; i<tbListestates.length; i++){ //loop through all form elements
				
				try{
				 myselect.add(new Option(tbListestates[i], i), null) //add new option
				}
				catch(e){ //in IE, try the below version instead of add()
				 myselect.add(new Option(tbListestates[i], i)) //add new option
				}
				
			}
			
		
		}
		
		
	   </script>
